import React from 'react';
import "../hojas-de-estilo/CarroPerfil.css";
import imgCarro from "../images/icon-cart.svg";
import imgAvatar from "../images/image-avatar.png";
import Articulos from '../components/Articulos';
let estadocar = 0;

function CarroPerfil({total, setTotal}){
    
    return(
        <div className='cont_carro_avatar'>
            <div>
            <div className='img_perfil'><img src={imgAvatar} alt="imagen_perfil" /></div>
            
            </div>
            <div>
                {total?
                <div className='mininumero'><span>{total}</span></div>
                :null}
                
                <div className='img_carro' onClick={abrircarrito}>
                <svg width="22" height="20" xmlns="http://www.w3.org/2000/svg">
                    <path d="M20.925 3.641H3.863L3.61.816A.896.896 0 0 0 2.717 0H.897a.896.896 0 1 0 0 1.792h1l1.031 11.483c.073.828.52 1.726 1.291 2.336C2.83 17.385 4.099 20 6.359 20c1.875 0 3.197-1.87 2.554-3.642h4.905c-.642 1.77.677 3.642 2.555 3.642a2.72 2.72 0 0 0 2.717-2.717 2.72 2.72 0 0 0-2.717-2.717H6.365c-.681 0-1.274-.41-1.53-1.009l14.321-.842a.896.896 0 0 0 .817-.677l1.821-7.283a.897.897 0 0 0-.87-1.114ZM6.358 18.208a.926.926 0 0 1 0-1.85.926.926 0 0 1 0 1.85Zm10.015 0a.926.926 0 0 1 0-1.85.926.926 0 0 1 0 1.85Zm2.021-7.243-13.8.81-.57-6.341h15.753l-1.383 5.53Z" 
                    className='colorcarro' fill-rule="nonzero"/></svg>
                </div>
                <div id='contcarrito' className='contcarrito'><Articulos total={total} setTotal={setTotal}></Articulos></div>
            </div>
        </div>
    );
}

function abrircarrito(){
    if(estadocar===0){
        document.getElementById("contcarrito").style.display="block" ;
        estadocar=1;
    }
    else{
        document.getElementById("contcarrito").style.display="none" ;
        estadocar=0;
    }
    
}

export default CarroPerfil;
