import React from 'react';
import "../hojas-de-estilo/Articulos.css";
import { useState } from 'react';

function Articulos(cantidad){

  const [cantidadar, setCantidad] = useState(0);
  return(
    <div className='con_articulos'>
      <div className='con_articulos_titulo'>
        <span>Cart</span>
      </div>
      <div className='con_articulos_lista'>
        {cantidadar?<div className='empty'><span>Your cart is empty.</span></div>:<div className='empty'><span>hay {cantidadar} elementos</span></div>
        }
        
        
      </div>
    </div>
  );
}

export default Articulos;
