import "../hojas-de-estilo/Articulos.css";
import { createContext, useState  } from 'react';
import ProductoDetalle from './ProductoDetalle';



const Articulos = ({props}) =>{

const {cantidad} = useContext(MyContext);

  return(
    <div className='con_articulos'>
      <div className='con_articulos_titulo'>
        <span>Cart</span>
      </div>
      <div className='con_articulos_lista'>
        {cantidad?<div className='empty'><span>hay {cantidad} elementos</span></div>:<div className='empty'><span>Your cart is empty.</span></div>
        }
        
        
      </div>
    </div>
  );
}

export default Articulos;
